get_ipython().run_cell_magic(u'HTML', u'', u'<!DOCTYPE html>\n<html>\n    <head>\n        <style> \n            body {\n                margin: 10px;\n                /*padding-top: 40px;*/\n            }\n        </style>\n    </head>\n\n    <body>\n        \n        <div style="width: 100%; overflow: hidden;">\n            <div id=\'jxgbox1\' class=\'jxgbox\' style=\'width:350px; height:300px; float:left; border: solid #1f628d 2px;\'></div>        \n            <div id=\'jxgbox2\' class=\'jxgbox\' style=\'width:350px; height:300px; margin-left: 375px; border: solid #1f628d 2px;\'></div>\n        </div>\n        \n        <!-- COMMENT: Where our Javascript begins. -->\n        <script type="text/javascript" src="https://cdnjs.cloudflare.com/ajax/libs/jsxgraph/0.98/jsxgraphcore.js"></script>\n        \n        <!-- COMMENT: Specific Davidson Next calls built on JSXGraph. Must be loaded after JSXgraph. -->\n        <script type="text/javascript" src="../../JS/Macro_t1.0.js"></script>\n        \n        <script type=\'text/javascript\'>\n            \n            //General Parameters for Macro\n            JXG.Options.point.showInfobox = false;\n            JXG.Options.segment.strokeColor = \'Gray\';\n            \n            ////////////\n            // BOARD 1\n            ////////////\n            bboxlimits = [-1.5, 12, 12, -1];\n            var brd1 = JXG.JSXGraph.initBoard(\'jxgbox1\', {axis:false, \n                                                    showCopyright: false,\n                                                    showNavigation: false,\n                                                    zoom: false,\n                                                    pan: false,\n                                                    boundingbox:bboxlimits,\n                                                    grid: true,\n                                                    hasMouseUp: true, \n            });         \n            \n            ////////////\n            // BOARD 2\n            ////////////\n            bboxlimits2 = [-2.0, 20, 20, -1];\n            var brd2 = JXG.JSXGraph.initBoard(\'jxgbox2\', {axis:false, \n                                                    showCopyright: false,\n                                                    showNavigation: false,\n                                                    zoom: false,\n                                                    pan: false,\n                                                    boundingbox:bboxlimits2,\n                                                    grid: true,\n                                                    hasMouseUp: true, \n            });        \n        \n    \n            \n            var A = brd1.create(\'point\',[1,1],{\'name\':\'A\'});\n            var B = brd2.create(\'point\',[1,1],{\'name\':\'B\'});\n            \n            \n            var S = brd1.create(\'segment\',[[1,1],[9,9]],{\'name\':\'S\'})\n            var D = brd1.create(\'segment\',[[1,9],[9,1]],{\'name\':\'D\'})\n            \n            var iSD = brd1.create(\'intersection\',[S,D],{\'name\':\'iSD\'})\n            \n            var tmpX = brd2.create(\'line\',[[10,0],[10,10]],{dash:1,visible:true});\n            var tmpY = brd2.create(\'line\',[[0,10],[10,10]],{dash:1,visible:true});\n            var reflectX = brd2.create(\'transform\',[tmpX],{type:\'reflect\'});\n            var reflectY = brd2.create(\'transform\',[tmpY],{type:\'reflect\'});\n            \n            //Board 2\n            var S2 = brd2.create(\'segment\',[[1,1],[9,9]],{\'name\':\'S\',color:\'DodgerBlue\'});\n            var B2 = brd2.create(\'point\',[[1,1],[9,9]],{\'name\':\'B\'});\n            var D2 = brd2.create(\'segment\',[[1,9],[9,1]],{\'name\':\'D\'});\n            \n            \n        </script>\n    </body>\n</html>')


